import React, { useEffect, useState } from "react";
import getPlates from "../helpers/getPlates";
import ListOrderDetail from "./ListOrderDetail";

const CardOrder = ({ orders }) => {
  const [plates, setPlates] = useState([]);

  const updatePlate = () => {
    getPlates()
      .then((newPlate) => setPlates(newPlate))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    updatePlate();
  }, []);

  return (
    <>
      {orders.map((order) => (
        <div key={order.command.id} className="col-12 col-sm-4 ">
          <div className="card m-2 mt-3">
            <div className="card-header">
              <div className="col-sm-4">Mesa: {order.command.tablet}</div>
              <div className="col-sm-4">Llego: {order.command.start}</div>
            </div>
            <div className="card-body">
              <ul className="list-group list-group-flush">
                {order.commandDetails.map((commandDetail) => (
                  <ListOrderDetail
                    key={commandDetail.id}
                    commandDetail={commandDetail}
                    plates={plates}
                    updatePlate={updatePlate}
                  />
                ))}
              </ul>
            </div>
          </div>
        </div>
      ))}
    </>
  );
};

export default CardOrder;
