import React from "react";
import postOrderUpdate from "../helpers/postOrderUpdate";

import io from "socket.io-client";

const socket = io("http://localhost:3030", { transports: ["websocket"] });

const ListOrderDetail = ({ commandDetail, plates, updatePlate }) => {
  const { id, plate_id, order_id, state, amount } = commandDetail;

  const getPlateName = () => {
    const platesNames = plates.filter((plate) => plate.id === plate_id);
    return platesNames.map((plateName) => plateName.name);
  };

  const changeOrderState = () => {
    postOrderUpdate({ id, plate_id, state: !state, amount, order_id }).then(
      (orderDetailNew) => console.log(orderDetailNew)
    );
    console.log("Esto estamo enviando", {
      id,
      plate_id,
      state: !state,
      amount,
      order_id,
    });
    socket.emit("OrderDetail:newOrderDetail");
  };
  return (
    <>
      <li key={id} className="list-group-item">
        <input type="checkbox" checked={state} onChange={changeOrderState} />
        <span className="badge badge-dark text-muted">X{amount}</span>
        {getPlateName()}
      </li>
    </>
  );
};

export default ListOrderDetail;
