import React from "react";
import postOrderUpdate from "../helpers/postOrderUpdate";
import {toast} from 'react-hot-toast'

const ListOrderDetail = ({ commandDetail, plates, updatePlate }) => {
  const { id, plate_id, state, amount } = commandDetail;

  const getPlateName = () => {
    const platesNames = plates.filter((plate) => plate.id === plate_id);
    return platesNames.map((plateName) => plateName.name);
  };

  const changeOrderState = () => {
    const resulOrder = postOrderUpdate({id, plate_id, state: !state, amount})
    console.log("Esto estamo enviando", {id, plate_id, state: !state, amount});
    console.log(resulOrder)
    toast.promise(resulOrder, {
      loading: "Cargando",
      success: "Se actualizo el pedido",
      error: "Error al actualizar"
    })
    updatePlate()
  };
  return (
    <>
      <li key={id} className="list-group-item">
        <input
          type="checkbox"
          checked={state}
          onChange={changeOrderState}
        />
        <span className="badge badge-dark text-muted">X{amount}</span>
        {getPlateName()}
      </li>
    </>
  );
};

export default ListOrderDetail;
