import Form from "../components/Form";
import Grid from "@mui/material/Grid";
import TextField from "@mui/material/TextField";
import TableC from "../components/Table";
import {
  Button,
  FormControl,
  InputLabel,
  MenuItem,
  Paper,
  Select,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from "@mui/material";
import { useState } from "react";

const initialStudent = {
  name: "",
  lastname: "",
  email: "",
  dni: "",
  phone: "",
  institute: "",
  career: "",
};

const listInstitute = [
  { value: "1", label: "Valle Grande" },
  { value: "2", label: "Condoray" },
];

const listCareersInitial = [
  { id: 1, value: "1", label: "Analisis de Sistemas" },
  { id: 1, value: "2", label: "Produccion Agraria" },
  { id: 2, value: "3", label: "Contador" },
  { id: 2, value: "4", label: "Administracion" },
];

const rowsStudent = [
  {
    id: 1,
    name: "Diego",
    lastName: "Huapaya Rivera",
    email: "diego.huapaya@vallegrande.edu.pe",
    dni: "84596235",
    phone: "987654321",
    institute: "Valle Grande",
    career: "Analisis de sistemas",
  },
  {
    id: 2,
    name: "Julio",
    lastName: "Quispe Chavez",
    email: "julio.quispe@vallegrande.edu.pe",
    dni: "84596235",
    phone: "987654321",
    institute: "Valle Grande",
    career: "Analisis de sistemas",
  },
  {
    id: 3,
    name: "Benjamin",
    lastName: "Diaz Medrano",
    email: "benjamin.diaz@vallegrande.edu.pe",
    dni: "84596235",
    phone: "987654321",
    institute: "Valle Grande",
    career: "Produccion Agraria",
  },
  {
    id: 4,
    name: "Juana",
    lastName: "Huamana Ramirez",
    email: "juana.huamana@vallegrande.edu.pe",
    dni: "84596235",
    phone: "987654321",
    institute: "Condoray",
    career: "Administracion",
  },
  {
    id: 5,
    name: "Malena Anicama",
    lastName: "Anicama Yataco",
    email: "malena.anicama@vallegrande.edu.pe",
    dni: "84596235",
    phone: "987654321",
    institute: "Condoray",
    career: "Hoteleria y turismo",
  },
];

export const Student = () => {
  const [student, setStudent] = useState(initialStudent);
  const [listCareers, setListCareers] = useState([]);

  const handleChangueStudent = (event) => {
    setStudent({ ...student, [event.target.name]: event.target.value });
  };

  const handleChangueStudentInstitute = (event) => {
    setStudent({ ...student, institute: event.target.value });
    setListCareers(
      listCareersInitial.filter((careers) => careers.id == student.institute)
    );
  };
  return (
    <>
      <Form title="Estudiante">
        <Grid container spacing={2}>
          <Grid item xs={6}>
            <TextField
              value={student.name}
              onChange={handleChangueStudent}
              name="name"
              label="Nombre"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Apellido"
              value={student.lastname}
              onChange={handleChangueStudent}
              name="lastname"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Email"
              value={student.email}
              onChange={handleChangueStudent}
              name="email"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="DNI"
              type="number"
              value={student.dni}
              onChange={handleChangueStudent}
              name="dni"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <TextField
              label="Celular"
              type="number"
              value={student.phone}
              onChange={handleChangueStudent}
              name="phone"
              variant="outlined"
            />
          </Grid>
          <Grid item xs={6}>
            <FormControl fullWidth>
              <InputLabel>Instituto</InputLabel>
              <Select
                label="Instituto"
                value={student.institute}
                onChange={handleChangueStudentInstitute}
              >
                <MenuItem value="">
                  <em>Ninguno</em>
                </MenuItem>
                {listInstitute.map((institute) => (
                  <MenuItem value={institute.value}>{institute.label}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={6}>
            <FormControl fullWidth>
              <InputLabel>Carrera</InputLabel>
              <Select
                label="Carrera"
                labelId="demo-simple-select-label"
                value={student.career}
                name="career"
                onChange={handleChangueStudent}
              >
                {listCareers.length == 0 ? (
                  <MenuItem value="">
                    <em>Ninguno</em>
                  </MenuItem>
                ) : (
                  listCareers.map((career) => (
                    <MenuItem value={career.value}>{career.label}</MenuItem>
                  ))
                )}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <Button variant="contained">Enviar</Button>
          </Grid>
        </Grid>
      </Form>
      <TableC title="Lista de estudiantes">
        <TableContainer component={Paper}>
          <Table aria-label="customized table">
            <TableHead>
              <TableCell>Nombre</TableCell>
              <TableCell>Apellido</TableCell>
              <TableCell>Correo</TableCell>
              <TableCell>Celular</TableCell>
              <TableCell>DNI</TableCell>
              <TableCell>instituto</TableCell>
              <TableCell>Carrera</TableCell>
            </TableHead>
            <TableBody></TableBody>
          </Table>
        </TableContainer>
      </TableC>
    </>
  );
};

export default Student;
