import React, { useState, useEffect } from "react";
import getPlates from "../helpers/getPlates";

const initialOrder = {
  plate_id: '',
  table: '',
  amount: '',
  observation: '',
}

const Card = () => {
  const [plates, setPlates] = useState([]);
  const [order, setOrder] = useState(initialOrder)

  const uploadPlate = () => {
    getPlates().then((newPlates) => {
      setPlates(newPlates);
    });
  };

  useEffect(() => {
    uploadPlate();
  }, []);

  const onSubmit = (e) => {
    e.preventDefault();
    console.log(order);
  };

  return (
    <div className="card p-3">
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label className="col-form-label">Mesa</label>
          <input
          value={order.table}
            name="table"
            className="form-control"
            type="number"
            max="10"
            min="0"
          ></input>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Comida</label>
          <select className="form-select form-control" value={order.plate_id}>
            <option value="">Seleccione un plato</option>
            {plates.map((plate) => (
              <option key={plate.id} value={plate.id}>
                {plate.name}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Cantidad</label>
          <input className="form-control" type="number" min="0"></input>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Observaci√≥n</label>
          <textarea className="form-control" type="number" min="0"></textarea>
        </div>
        <button className="btn btn-primary btn-block">Enviar</button>
      </form>
    </div>
  );
};

export default Card;
