import React from "react";

const Card = ({ plates, order }) => {
  const mainOrder = order;

  const onSubmit = (e) => {
    console.log(e);
  };

  return (
    <div className="card p-3">
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label className="col-form-label">Mesa</label>
          <input
            className="form-control"
            type="number"
            max="10"
            min="0"
          ></input>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Comida</label>
          <select className="form-select form-control" value={mainOrder.id_plate}>
            <option selected>Seleccione un plato</option>
            {plates.map((plate) => (
              <option key={plate.id}>{plate.name}</option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Cantidad</label>
          <input className="form-control" type="number" min="0"></input>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Observaci√≥n</label>
          <textarea className="form-control" type="number" min="0"></textarea>
        </div>
        <button className="btn btn-primary btn-block">Enviar</button>
      </form>
    </div>
  );
};

export default Card;
