import React, { useState } from "react";
import Card from "../components/Card";
import Table from "../components/Table";
import postOrder from "../helpers/postOrder";

const initialOrder = {
  plate_id: "",
  table: "",
  amount: "",
  observation: "",
  name: "",
};

const Main = () => {
  const [order, setOrder] = useState(initialOrder);
  const [orders, setOrders] = useState([]);

  const addOrderList = () => {
    setOrders([...orders, order]);
  };

  const sendOrder = () => {
    const platesDTO = [];
    orders.forEach((order) => {
      const { plate_id, amount, observation } = order;
      platesDTO.push({ plate_id, amount, observation });
    });
    const orderDTO = {
      table: order.table,
      plates: platesDTO,
    };
    postOrder(orderDTO).then((newOrder) => console.log(newOrder));
  };

  return (
    <div className="row justify-content-md-center">
      <div className="col-12 col-sm-4 pt-2">
        <Card
          order={order}
          setOrder={setOrder}
          addOrderList={addOrderList}
          sendOrder={sendOrder}
        />
      </div>
      <div className="col-12 col-sm-4 pt-2">
        <Table orders={orders} setOrders={setOrders} setOrder={setOrder} />
      </div>
    </div>
  );
};

export default Main;
