import React, { useState, useEffect } from "react";
import getPlates from "../helpers/getPlates";

const Card = ({ order, setOrder, form }) => {
  const [plates, setPlates] = useState([]);

  const uploadPlate = () => {
    getPlates().then((newPlates) => {
      setPlates(newPlates);
    });
  };

  useEffect(() => {
    uploadPlate();
  }, []);

  const onSubmit = (e) => {
    e.preventDefault();
    form();
  };

  const onChange = (e) => {
    if (order.plate_id !== "0" || order.plate_id !== "") {
      const selectPlate = plates.filter((plate) => plate.id == order.plate_id);
      if (selectPlate.length > 0) {
        const namePlate = selectPlate[0].name;
        console.log(typeof namePlate);
        setOrder({ ...order, [e.target.name]: e.target.value, name: namePlate });
        console.log(order)
      }
    }
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  return (
    <div className="card p-3">
      <form onSubmit={onSubmit}>
        <div className="mb-3">
          <label className="col-form-label">Mesa</label>
          <input
            onChange={onChange}
            value={order.table || ""}
            name="table"
            className="form-control"
            type="number"
            max="10"
            min="0"
          ></input>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Comida</label>
          <select
            className="form-select form-control"
            onChange={onChange}
            value={order.plate_id || ""}
            name="plate_id"
          >
            <option value="0">Seleccione un plato</option>
            {plates.map((plate) => (
              <option key={plate.id} value={plate.id}>
                {plate.name}
              </option>
            ))}
          </select>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Cantidad</label>
          <input
            className="form-control"
            type="number"
            min="0"
            value={order.amount || ""}
            name="amount"
            onChange={onChange}
          ></input>
        </div>
        <div className="mb-3">
          <label className="col-form-label">Observación</label>
          <textarea
            className="form-control"
            type="number"
            value={order.observation || ""}
            name="observation"
            onChange={onChange}
          ></textarea>
        </div>
        <button className="btn btn-primary btn-block">Enviar</button>
      </form>
    </div>
  );
};

export default Card;
